import React, { useState } from "react";

function Form() {
  const [firstName, setFirst] = useState("");
  const [lastName, setLast] = useState("");
  const [paragraph,setParagraph]  = useState([]);

  const nameSubmit = (e)=>{
    e.preventDefault();

      if(firstName&&lastName){
        setFirst("")
        setLast("")
        setParagraph(()=>[firstName,lastName])

      }
  }

  return (
    <div>
    <form onSubmit={nameSubmit}>
      <label>
        First Name:
        <input
          value={firstName}
          name="firstName"

          //onchange event listens to the input
          onChange={(e) => {
             console.log(e.target.value);
            setFirst(e.target.value);
          }}
          type="text"
          autoComplete="off"
        />
      </label>
      <label>
        Last Name:
        <input
          value={lastName}
          name="lastName"
          onChange={(e) => {
             console.log(e.target.value);
            setLast(e.target.value);
          }}
          type="text"
          autoComplete="off"
        />
      </label>
      <button type="submit" >
        Submit
      </button>
    </form>

    {
      paragraph.map((paragraph)=><div>
        <p>{paragraph}  </p>
      </div>)
    }
    </div>

  );
}
export default Form;
