import React, { useState } from "react";

function Form() {
  const [firstName, setFirst] = useState("");
  const [lastName, setLast] = useState("");
  const[add, setAdd] = useState([])



  function addFnames(e) {
    e.preventDefault();
    setFirst("");
    setLast("");
    console.log(firstName, lastName);
    return (
      <p>
        {firstName} {lastName}
      </p>
    );
  }
  const [dataInfo, setDataInfo] = useState({
    firstName:"",
    lastName:""
  })

  const info =[firstName, category, amount]
    if(date&&description&&category&&amount){
      setAdd((dataInfo)=>[...dataInfo,info])
      setDate("")
      setDescription("")
      setCategory("")
      setAmount("")
    }
  const handleSubmit = () => {
    <p>
      {firstName} {lastName}
    </p>;
  };

  return (
    <div>
    <form onSubmit={addFnames}>
      <label>
        First Name:
        <input
          value={firstName}
          name="firstName"
          id="firstName"
          onChange={(e) => {
            console.log(e.target.value);
            setFirst(e.target.value);
          }}
          type="text"
        />
      </label>
      <label>
        Last Name:
        <input
          value={lastName}
          name="lastName"
          id="lastName"
          onChange={(e) => {
            console.log(e.target.value);
            setLast(e.target.value);
          }}
          type="text"
        />
      </label>
      <button type="submit" onClick={handleSubmit}>
        Submit
      </button>
    </form>
    {
      add.map((dataInfo)=>
      <div>
        <p>{dataInfo.firstName}</p>
        <p>{dataInfo.lastName}</p>
      </div>)
    }
    </div>
  );
}

export default Form;
