import React, { useState } from "react";
// import DisplayData from "./DisplayData";


function Form() {
  const [firstName, setFirst] = useState("");
  const [lastName, setLast] = useState("");
  const [paragraph,setParagraph]  = useState([]);

  // function addFnames(e) {
  //   e.preventDefault();
  //   setFirst("");
  //   setLast("");
  //   console.log(firstName, lastName);
  //   alert(`My name is ${firstName} ${lastName}`)

  // }

  const nameSubmit = (e)=>{
    e.preventDefault();
      //  <p>My name is {firstName} {lastName}</p>
      const data =(firstName,lastName)
      if(firstName&&lastName){
        setParagraph((p)=>[...p,data])
        setFirst('')
        setLast('')
      }
  }

  return (
    <div>
      <h1>Group 1 - Form Submit</h1>
    <form onSubmit={nameSubmit}>
      <label>
        First Name:
        <input
          value={firstName}
          name="firstName"
          onChange={(e) => {
            // console.log(e.target.value);
            setFirst(e.target.value);
          }}
          type="text"
        />
      </label>
      <label>
        Last Name:
        <input
          value={lastName}
          name="lastName"
          onChange={(e) => {
            // console.log(e.target.value);
            setLast(e.target.value);
          }}
          type="text"
        />
      </label>
      <button type="submit" >
        Submit
      </button>
    </form>

    {
      paragraph.map((firstName,lastName)=><div>
        <p>{prop.firstName} {prop.lastName}</p>
      </div>)
    }
    {/* {
