import React, { useState } from "react";
import ReactDOM from "react-dom/client";


function Form() {
  const [firstName, setFirst] = useState("");
  const [lastName, setLast] = useState("");

  function addFnames(e) {
    e.preventDefault();
    setFirst("");
    setLast("");
    console.log(firstName, lastName);
    // return (
    //   <p>
    //     {firstName} {lastName}
    //   </p>
    // );

  }


  // const handleSubmit = () => {
  //   <p>
  //     {firstName} {lastName}
  //   </p>;
  // };

  function nameSubmit (){
    return (
      <>
      <p>My name is {firstName} {lastName}</p>
      </>
    )
  }

  return (
    <div>
    <form onSubmit={addFnames}>
      <label>
        First Name:
        <input
          value={firstName}
          name="firstName"
          onChange={(e) => {
            console.log(e.target.value);
            setFirst(e.target.value);
          }}
          type="text"
        />
      </label>
      <label>
        Last Name:
        <input
          value={lastName}
          name="lastName"
          onChange={(e) => {
            console.log(e.target.value);
            setLast(e.target.value);
          }}
          type="text"
        />
      </label>
      <button type="submit" onClick={nameSubmit}>
        Submit
      </button>
    </form>
    {/* {
